import React, { useState, useEffect } from "react";
import styled from "styled-components";
import loadable from "@loadable/component";
import {
  faYoutube,
  faFontAwesomeFlag,
} from "@fortawesome/free-brands-svg-icons";
import {
  faSearch,
  faEllipsisV,
  faCaretDown,
  faShare,
  faPlus,
} from "@fortawesome/free-solid-svg-icons";
import { faThumbsUp, faThumbsDown } from "@fortawesome/free-regular-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Home: React.FC = () => {
  //   const [isToggle, setIsToggle] = useState<boolean>(false);
  //   const [buttonColor, setButtonColor] = useState<string>("white");

  //   const onClickButton = () => {
  //     setIsToggle(!isToggle);
  //   };

  //   useEffect(() => {
  //     window.alert("유리바보");
  //   }, []);

  //   useEffect(() => {
  //     if (isToggle) {
  //       setButtonColor("yellow");
  //     } else {
  //       setButtonColor("white");
  //     }
  //   }, [isToggle]);

  return (
    <Container>
      {/* Header */}
      <Header>
        <Logo>
          <FontAwesomeIcon icon={faYoutube} size="2x" />
          <Title>Youtube</Title>
        </Logo>
        <Icon>
          <FontAwesomeIcon icon={faSearch} size="2x" />
          <FontAwesomeIcon icon={faEllipsisV} size="2x" />
        </Icon>
      </Header>

      {/* Video Player */}
      <Player>
        <Video controls src="./components/Cat.mp4" />
      </Player>

      <InfoAndUpNext>
        {/* Video Info */}
        <Info>
          {/* Meta Data */}
          <Metadata>
            <Hashtags>
              <li>#Yuri</li>
              <li>#Cat</li>
              <li>#Cute</li>
            </Hashtags>
            <TitleAndButton>
              <Title>유리는 고양이를 좋아합니다.</Title>
              <MoreBtn>
                <FontAwesomeIcon icon={faCaretDown} size="2x" />
              </MoreBtn>
            </TitleAndButton>
            <Views>1M views 1 day ago</Views>
          </Metadata>

          {/* Actions */}
          <Actions>
            <li>
              <button>
                <FontAwesomeIcon icon={faThumbsUp} size="2x" />
                <span>1K</span>
              </button>
            </li>
            <li>
              <button>
                <FontAwesomeIcon icon={faThumbsDown} size="2x" />
                <span>0</span>
              </button>
            </li>
            <li>
              <button>
                <FontAwesomeIcon icon={faShare} size="2x" />
                <span>Share</span>
              </button>
            </li>
            <li>
              <button>
                <FontAwesomeIcon icon={faPlus} size="2x" />
                <span>Save</span>
              </button>
            </li>
            <li>
              <button>
                <FontAwesomeIcon icon={faFontAwesomeFlag} size="2x" />
                <span>Report</span>
              </button>
            </li>
          </Actions>

          {/* Channel Description */}
          <Channel>
            <Metadata>
              <img src="image/snoopy.jpg" alt="" />
              <Info>
                <Name></Name>
                <Subscribers></Subscribers>
              </Info>
            </Metadata>
            <Subscribe>subscribe</Subscribe>
          </Channel>
        </Info>

        {/* Up Next */}
        <UpNext>
          <Title>Up Next</Title>
          <ul>
            <Item>
              <Img>
                <img src="/components/snoopy.jpg" alt="" />
              </Img>
              <ItemInfo>
                <Title>유리는 강아지도 좋아한다.</Title>
                <Name>유리</Name>
                <Views>82K views</Views>
              </ItemInfo>
              <MoreBtn>
                <FontAwesomeIcon icon={faEllipsisV} size="2x" />
              </MoreBtn>
            </Item>
          </ul>
        </UpNext>
      </InfoAndUpNext>

      <Main>
        {/* <Button buttonColor={buttonColor} onClick={onClickButton}>
          <p>{isToggle ? "유리 바보" : "콩이 귀여워"}</p>
        </Button> */}
      </Main>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  height: 100%;
`;

const Title = styled.span``;

const Header = styled.div``;

const Logo = styled.div``;

const Icon = styled.div``;

const Player = styled.section``;

const Video = styled.video``;

const InfoAndUpNext = styled.div``;

const Info = styled.section``;

const Metadata = styled.div``;

const Hashtags = styled.ul``;

const TitleAndButton = styled.div``;

const MoreBtn = styled.button``;

const Views = styled.span``;

const Actions = styled.ul``;

const Channel = styled.div``;

const Name = styled.span``;

const Subscribers = styled.span``;

const Subscribe = styled.button``;

const UpNext = styled.section``;

const Item = styled.li``;

const Img = styled.div``;

const ItemInfo = styled.div``;

const Main = styled.div`
  width: 100%;
  flex: 1 1 auto;
  padding-top: 48px;
`;

const Button = styled.div<any>`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100px;
  height: 48px;
  background: ${(props: any) => props.buttonColor};
  border: 1px solid #dee1e6;
`;

export default Home;
